import { Component } from '@angular/core';

@Component({
  selector: 'app-order',
  templateUrl: './order.component.html',
  styleUrls: ['./order.component.scss']
})
export class OrderComponent {
  order = {
    item: '',
    price: 0,
    amount: 0
  };

  constructor() {}

  onSubmit() {
    console.log('Dados do pedido:', this.order);
  }
}
<div class="container mt-5">
    <h2>Cadastrar Pedido</h2>
    <form (submit)="onSubmit()">
        <div class="form-group">
            <label for="item">Item</label>
            <input type="text" class="form-control mb-2" id="item" [(ngModel)]="order.item" name="item" required>

            <label for="price">Preço</label>
            <input type="number" class="form-control mb-2" id="price" [(ngModel)]="order.price" name="price" required>

            <label for="amount">Quantidade</label>
            <input type="number" class="form-control mb-2" id="amount" [(ngModel)]="order.amount" name="amount" required>

            <button routerLink="/home" type="button" class="btn btn-secondary mr-2">Voltar</button>
            <button type="submit" class="btn btn-success">Salvar</button>
        </div>
    </form>
</div>
