import { Component } from '@angular/core';

@Component({
  selector: 'app-customer',
  templateUrl: './customer.component.html',
  styleUrls: ['./customer.component.scss']
})
export class CustomerComponent {
  customer: string;

  constructor() {
    this.customer = '';
  }

  onSubmit() {
    console.log('Cliente:', this.customer);
  }
}
<div class="container mt-5">
    <h2>Cadastrar Cliente</h2>
    <form (submit)="onSubmit()">
        <div class="form-group">
            <label for="name">Nome</label>
            <input type="text" class="form-control mb-2" id="name" [(ngModel)]="customer.name" name="name" required>

            <label for="cpfCnpj">CPF/CNPJ</label>
            <input type="text" class="form-control mb-2" id="cpfCnpj" [(ngModel)]="customer.cpfCnpj" name="cpfCnpj" required>

            <label for="cellphone">Celular</label>
            <input type="text" class="form-control mb-2" id="cellphone" [(ngModel)]="customer.cellphone" name="cellphone" required>

            <label for="address">Endereço</label>
            <input type="text" class="form-control mb-2" id="address" [(ngModel)]="customer.address" name="address" required>

            <!-- Campos adicionais para dados de endereço podem ser adicionados aqui -->

            <button routerLink="/home" type="button" class="btn btn-secondary mr-2">Voltar</button>
            <button type="submit" class="btn btn-success">Salvar</button>
        </div>
    </form>
</div>
