import { Component } from '@angular/core';

@Component({
  selector: 'app-customer',
  templateUrl: './customer.component.html',
  styleUrls: ['./customer.component.scss']
})
export class CustomerComponent {
  customer: string;

  constructor() {
    this.customer = '';
  }

  onSubmit() {
    console.log('Cliente:', this.customer);
  }
}
