import { Component } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { NgModule } from '@angular/core';
import { BrowserModule } from '@angular/platform-browser';
import { ReactiveFormsModule } from '@angular/forms';  // Importe o ReactiveFormsModule aqui

interface Item {
  name: string;
  price: number;
  quantity: number;
}

@Component({
  selector: 'app-scheduler',
  templateUrl: './scheduler.component.html',
  styleUrls: ['./scheduler.component.scss']
})
export class SchedulerComponent {

  constructor(private fb: FormBuilder) {}

  ngOnInit(): void {
    this.yourForm = this.fb.group({
      yourControl: [null]  // valor inicial é null
    });
  }

  yourForm!: FormGroup;  // Note o operador '!' aqui
  options: { label: string, value: string }[] = [
    { label: 'Opção 1', value: '1' },
    { label: 'Opção 2', value: '2' },
    // ... outras opções
  ];

  newItem: Item = {
    name: '',
    price: 0,
    quantity: 0
  };

  order = {
    items: [] as Item[]
  };



  addItem() {
    this.order.items.push({ ...this.newItem });
    this.newItem = {
      name: '',
      price: 0,
      quantity: 0
    };
  }

  onSubmit() {
    console.log('Pedido:', this.order);
  }
}
<div class="container mt-5">
    <h2>Agendador</h2>
    <form (submit)="onSubmit()">
        <div class="form-group">
            <label for="item">Cliente</label>
            <input type="text" class="form-control mb-2" id="item" [(ngModel)]="newItem.name" name="item" required>

            <form [formGroup]="yourForm">
                <select formControlName="yourControl">
                    <option *ngFor="let option of options" [value]="option.value">{{ option.label }}</option>
                </select>
            </form>


            <label for="price">Mensagem</label>
            <input type="text" class="form-control mb-2" id="price" [(ngModel)]="newItem.price" name="price" required>

            <label for="quantity">Pedido</label>
            <input type="text" class="form-control mb-2" id="quantity" [(ngModel)]="newItem.quantity" name="quantity" required>

            <button type="button" class="btn btn-primary mr-2" (click)="addItem()">Adicionar</button>
            <button type="submit" class="btn btn-success">Finalizar Pedido</button>
        </div>
    </form>

    <h3>Itens do Pedido</h3>
    <ul>
        <li *ngFor="let item of order.items">
            {{ item.name }} - {{ item.price }} - {{ item.quantity }}
        </li>
    </ul>
</div>
