import { Component } from '@angular/core';

@Component({
  selector: 'app-message',
  templateUrl: './message.component.html',
  styleUrls: ['./message.component.scss']
})
export class MessageComponent {
  message: string;

  constructor() {
    this.message = '';
  }

  onSubmit() {
    console.log('Mensagem:', this.message);
  }
}
